package com.yksj.consultation.son.pay;import android.text.TextUtils;import java.util.Map;public class PayResult {    private String resultStatus;    private String result;    private String memo;    public PayResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult.get(key);            } else if (TextUtils.equals(key, "result")) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult.get(key);            }        }    }    public PayResult(String rawResult) {        if (TextUtils.isEmpty(rawResult))            return;        String[] resultParams = rawResult.split(",");        for (String resultParam : resultParams) {            if (resultParam.startsWith("{resultStatus")) {                resultStatus = resultParam.substring(14);            }            if (resultParam.startsWith("result")) {                result = resultParam.substring(7);            }            if (resultParam.startsWith("memo")) {                memo = resultParam.substring(5, resultParam.length() - 1);            }        }    }    @Override    public String toString() {        return "resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "}";    }    private String gatValue(String content, String key) {        String prefix = key + "={";        return content.substring(content.indexOf(prefix) + prefix.length(),                content.lastIndexOf("}"));    }    /**     * @return the resultStatus     */    public String getResultStatus() {        return resultStatus;    }    /**     * @return the memo     */    public String getMemo() {        return memo;    }    /**     * @return the result     */    public String getResult() {        return result;    }}